import {makeAutoObservable} from "mobx";
import {ITask, ITaskForm} from "../type/task";

class Task {
    constructor() {
        makeAutoObservable(this);
    };

    tasks: ITask[] = [
        {
            id: 1,
            name: '1',
            description: '1'
        },
        {
            id: 2,
            name: '2',
            description: '2'
        },
        {
            id: 3,
            name: '1.1',
            description: '1.1',
            parentTaskId: 1
        }
    ] as ITask[];
    setTasks = (data: ITask[]) => this.tasks = data;

    task: ITask | undefined = {} as ITask | undefined;
    setTask = (data: ITask) => this.task = data;

    addTask = (data: ITaskForm) => {
        const id = this.tasks.length;
        if (!data.parentTaskId) {
            this.setTasks([...this.tasks, {
                id: id,
                name: data.name,
                description: data.description,
                children: []
            }])
        } else {
            this.setTasks([this.tasks.map(task => {
                if (task.id === data.parentTaskId) {
                    return {
                        id: task.id,
                        name: task.name,
                        description: task.description,
                        children: [...task.children, {
                            id: id,
                            name: data.name,
                            description: data.description,
                            children: []
                        }]
                    }
                } else {
                    return task
                }
            })])
        }

        return this.tasks
    }

    getTask = (taskId: number) => {
        this.setTask(this.tasks.filter(task => task.id === taskId)[0])
    }

    getChildrenTasks = (parentTaskId: number) => {
        return (
            this.tasks.filter(task => task.parentTaskId === parentTaskId).length > 0
                ?
                this.tasks.filter(task => task.parentTaskId === parentTaskId)
                : undefined
        )
    }
}

// eslint-disable-next-line
export default new Task()
