import {makeAutoObservable} from "mobx";
import {ITask, ITaskForm} from "../type/task";

class Task {
    constructor() {
        makeAutoObservable(this);
    };

    tasks: ITask[] = [] as ITask[];
    setTasks = (data: ITask[]) => this.tasks = data;

    task: ITask | undefined = {} as ITask | undefined;
    setTask = (data: ITask) => this.task = data;

    addTask = (data: ITaskForm) => {
        const id = this.tasks.length;
        this.setTasks([...this.tasks, {
            id: id,
            name: data.name,
            description: data.description,
            parentTask: data.parentTask}
        ])

        return this.tasks
    }

    getTask = (taskId: number) => {
        this.setTask(this.tasks[this.tasks.indexOf({id: taskId, name: 'fghfgh'})])
    }
}

// eslint-disable-next-line
export default new Task()
