import {makeAutoObservable} from "mobx";
import {ITask, ITaskForm} from "../type/task";

class Task {
    constructor() {
        makeAutoObservable(this);
    };

    tasks: ITask[] = [] as ITask[];
    setTasks = (data: ITask[]) => this.tasks = data;

    addTask = (data: ITaskForm) => {
        const id = this.tasks.length;
        if (!data.parentTask) {
            this.setTasks([...this.tasks, {id: id, name: data.name, description: data.description}])
        } else {
            this.setTasks([this.tasks.map(task => (
                task.id === data.parentTask
                ?
                    {
                        id: task.id,
                        name: task.name,
                        description: task.description,
                        children:  [{id: id, name: data.name, description: data.description}]
                    }
                :
                    task
            ))])
        }

        return this.tasks
    }
}

// eslint-disable-next-line
export default new Task()
