import React, {FC} from 'react';
import {ITaskListItemProps} from "./ITaskListItemProps";
import tasks from "../../store/Task";

const TaskListItem: FC<ITaskListItemProps> = ({task}) => {
    return (
        <div>
            {task.name}
            <>
                {
                    tasks.getChildrenTasks(task.id)?.map(childrenTask =>
                        <TaskListItem task={childrenTask} style={{margin: '10px 15px'}}/>
                    )
                }
            </>
        </div>
    );
};

export default TaskListItem;
