import {makeAutoObservable} from "mobx";
import {ITask, ITaskForm} from "../type/task";

class Task {
    constructor() {
        makeAutoObservable(this);
    };

    tasks: ITask[] = [
        {
            id: 1,
            name: '1',
            description: '1',
            children: [
                {
                    id: 3,
                    name: '1.1',
                    description: '1',
                    children: [
                        {
                            id: 4,
                            name: '1.1.1',
                            description: '1',
                        }
                    ]
                }
            ]
        },
        {
            id: 2,
            name: '2',
            description: '2'
        },
    ] as ITask[];
    setTasks = (data: ITask[]) => this.tasks = data;

    task: ITask | undefined = {} as ITask | undefined;
    setTask = (data: ITask) => this.task = data;

    addTask = (data: ITaskForm) => {
        if (!data.parentTaskId) {
            this.setTasks([...this.tasks, {
                id: this.tasks.length,
                name: data.name,
                description: data.description,
                children: []
            }])
        } else {
            this.setTasks(this.tasks.map(task => (
                task.id === data.parentTaskId
                ?
                    {
                        id: task.id,
                        name: task.name,
                        description: task.description,
                            children: [...task.children, {
                                id: task.children.length,
                                name: data.name,
                                description: data.description,
                                children: []
                            }]
                    }
                : task
            )))
        }

        return this.tasks
    }

    getTask = (taskId: number) => {
        this.setTask(this.tasks.filter(task => task.id === taskId)[0])
    }
}

// eslint-disable-next-line
export default new Task()
