import React, {FC} from 'react';
import {ITaskListItemProps} from "./ITaskListItemProps";

const TaskListItem: FC<ITaskListItemProps> = ({task, style}) => {
    return (
        <div style={style}>
            {task.name}
            <>
                {
                    task.children
                        ?
                        task.children.map(childrenTask =>
                            <TaskListItem task={childrenTask} style={{margin: '10px 15px'}} key={childrenTask.id}/>
                        )
                        : null
                }
            </>
        </div>
    );
};

export default TaskListItem;
