import React, {FC, useEffect, useState} from 'react';
import {ITaskListItemProps} from "./ITaskListItemProps";
import {ITask} from "../../type/task";
import tasks from "../../store/Tasks";
import style from './TaskListItem.module.scss'

const TaskListItem: FC<ITaskListItemProps> = ({task, isChildren}) => {
    const [childrenList, setChildrenList] = useState<ITask[]>();

    useEffect(() => {
        setChildrenList(tasks.getChildrenTaskList(task.id))
    }, [tasks.tasks])

    return (
        <>
            {
                !isChildren
                ?
                    <div>
                        <div className={style.task}>{task.name}</div>
                        <>
                            {
                                childrenList
                                    ?
                                    childrenList.map(childrenTask =>
                                        <TaskListItem task={childrenTask} isChildren={true} key={childrenTask.id}/>
                                    )
                                    : null
                            }
                        </>
                    </div>
                    :
                    <div>
                        <div className={style.children__task}>{task.name}</div>
                        <>
                            {
                                childrenList
                                    ?
                                    childrenList.map(childrenTask =>
                                        <TaskListItem task={childrenTask} isChildren={true} key={childrenTask.id}/>
                                    )
                                    : null
                            }
                        </>
                    </div>
            }
        </>
        // <div style={childrenStyle}>
        //     <div className={style.task}>
        //         <p>{task.name}</p>
        //     </div>
        //     <>
        //         {
        //             childrenList
        //                 ?
        //                 childrenList.map(childrenTask =>
        //                     <TaskListItem task={childrenTask} childrenStyle={{margin: '10px 15px'}} key={childrenTask.id}/>
        //                 )
        //                 : null
        //         }
        //     </>
        // </div>
    );
};

export default TaskListItem;
